def ziehen(x):
    Gezogen =[]
    Bedeutungen=[]
    Anderebedeutungen=[]
    for _ in range(x):
        index= random.randint(0,78)
        Gezogen.append(Karten[index])
        rev= random.randint(0,1)
        if rev == 0:
            Bedeutungen.append(Bedeutung[index])
            print(index)
        else:
            Anderebedeutungen.append(Gedreht[index])
    return Gezogen,Bedeutungen, Anderebedeutungen


